wp.customize("logo",function(o){o.bind(function(o){o.length>0?wp.customize.control("logo_retina").activate():wp.customize.control("logo_retina").deactivate()})}),wp.customize("layout",function(o){o.bind(function(o){o===!0?wp.customize.control("layout_box_width").activate():wp.customize.control("layout_box_width").deactivate()})}),wp.customize("layout_fixed",function(o){o.bind(function(o){o===!0?wp.customize.control("layout_grid_width").deactivate():wp.customize.control("layout_grid_width").activate()})}),wp.customize("code",function(o){o.bind(function(o){o===!0?(wp.customize.control("code_head").activate(),wp.customize.control("code_body").activate()):(wp.customize.control("code_head").deactivate(),wp.customize.control("code_body").deactivate())})}),wp.customize("footer_logo",function(o){o.bind(function(o){o.length>0?wp.customize.control("footer_logo_retina").activate():wp.customize.control("footer_logo_retina").deactivate()})}),wp.customize("nav_menu_locations[footer_navigation]",function(o){o.bind(function(o){parseInt(o)>0?wp.customize.control("footer_top").activate():wp.customize.control("footer_top").deactivate()})});